INSERT INTO "ADJ_GRO_CODE" VALUES (2, 20180505000040, '**Pursuant to Section 71(1) of the Bankruptcy Ordinance, interest for purposes of dividend can be calculated only up to the date of the receiving order (i.e.                  ) and at rate not exceeding 8% per annum; interest in excess of that rate is a deferred debt.', '2', '**依據破產條例第71(1)條的規定，為處理債款而計算有關利息時，只可計算至頒布接管令的日期 (即              ) 止，並且按年息不超逾 8 厘計算；而超逾上述年息的餘額，則只能作延期債款處理。', 2, 'ACT', 1, 'admin', 'test33', TO_DATE('20180505115026', 'YYYYMMDDHH24MISS'), 'admin', 'test33', TO_DATE('20180505115109', 'YYYYMMDDHH24MISS'), 'ORD');
