INSERT INTO "ADJ_GRO_CODE" VALUES (10, 20180507000102, 'S71 of the Bankruptcy Ordinance provides that for payment of interest under s38(9), the interest rate to be applied shall be the rate applicable under s49 of the High Court Ordinance at the commencement of the bankruptcy ("the Judgment Rate") or the contractual rate, whichever is greater.', '1', '《破产条例》第七十一条规定，根据S38（9）的利息支付，申请的利率应为《高等法院条例》第49条在破产开始时（“判决率”）或合同利率下适用的比率，以较大者为准!', 1, 'INA', 2, 'admin', 'test33', TO_DATE('20180507144729', 'YYYYMMDDHH24MISS'), 'admin', 'test33', TO_DATE('20180507151158', 'YYYYMMDDHH24MISS'), 'INT');
