INSERT INTO "ADJ_GRO_CODE" VALUES (13, 20180507000094, 'Pursuant to Section 71(1) of the Bankruptcy Ordinance, where a bankruptcy debt bears interest, that interest is provable as part of the debt except in so far as it is payable in respect of any period after the commencement of the bankruptcy.', '1', '根據破產條例第 71(1)條，凡任何破產債項是衍生利息的，則該利息即為該債項的部分可證債項，但如該利息就該破產開始後的任何期間是須支付', 1, 'ACT', 0, 'admin', 'test33', TO_DATE('20180507151046', 'YYYYMMDDHH24MISS'), 'admin', 'test33', TO_DATE('20180507151046', 'YYYYMMDDHH24MISS'), 'ORD');
